import React, { Component } from "react";
// import { Link } from "react-router-dom";
// import Row from "../components/Row";
// import Col from "../components/Col";
// import Container from "../components/Container";
// import axios from "axios";
// import Jumbotron from "../components/Jumbotron";
import Container from "../components/Container";
import DetailResults from "../components/DetailResults";
import SearchResults from "../components/SearchResults";
import API from "../utils/API";

let bookInfo =[]
class Detail extends Component {
  state = {
    id: "",      
    book: [],    
    error: ""
  };
  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc



  // searchById = id =>{
  //   API.searchId(this.props.match.params.id)
  //   .then(res => 
  //     this.setState({ book: res.data.items })
  //     )      
  //     .catch(err => console.log(err));
  // }



  searchById = keyId => {
      API.search(keyId)
        .then(res => this.setState({ books: res.data.items }))
        .catch(err => console.log(err));
    }

    handleInputChange = event => {
      const name = event.target.name;
      const value = event.target.value;
      this.setState({
        [name]: value
      });
    };

    handleDetailSubmit = event => {
      event.preventDefault();
      this.searchById(this.props.match.params.id);         
    };


    render() {
      return (
        <div>
          <Container style={{ minHeight: "80%" }}>          

            <DetailResults
              id={this.state.id}
              book={this.state.book}

            />
            <SearchResults handleDetailSubmit={this.handleDetailSubmit}
            />

          </Container>
        </div>

      );
    }
  }


export default Detail;
