import React, { Component } from "react";
// import { Link } from "react-router-dom";
// import Row from "../components/Row";
// import Col from "../components/Col";
// import Container from "../components/Container";
// import axios from "axios";
// import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";

let bookInfo =[]
class Detail extends Component {
  state = {
    book: {}
  };
  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc

  componentDidMount () {
    this.searchById();
    // API.searchId(this.props.match.params.id)   
    //   .then(res => this.setState({ book: res.data }))
    //   .catch(err => console.log(err));
  }

  searchById = id =>{
    API.searchId(this.props.match.params.id)
    .then(res => 
      this.setState({ book: res.data.items })
      )      
      .catch(err => console.log(err));
  }

  // searchForBooks = query => {
  //   API.search(query)
  //     .then(res => this.setState({ books: res.data.items }))
  //     .catch(err => console.log(err));
  // }

  render() {
    bookInfo.push(this.state.book)
    return (

      <ul className="list-group search-results">
        {bookInfo.map(result => (        

          <li key={result.id} className="list-group-item">
            <img alt={result.title} className="img-fluid" src={result.imageLinks == null ? 'https://lmtrain.github.io/lm-images/assets/images/books5.jpg' : result.imageLinks.thumbnail} /><span>     </span>

            <p>Title : {result.title}</p>
            <span>Authors : {result.authors} | |</span>
            <span>Published Date : {result.publishedDate}</span>        
          </li>

        ))}

      </ul>
    );
  }
}
console.log(bookInfo)
export default Detail;
