import React from "react";
import { Link } from "react-router-dom";
import { List, ListItem } from "../components/List";
import "./style.css";



// favById = () => {
//   API.search()
//     .then(res => this.setState({ book: res.data.volumeInfo }))
//       this.saveFavBook()
//     .catch(err => console.log(err));
// }
let myTitle = ""
let myId = ""
let myAuthor = ""




// saveFavBook = () => {
//   API.saveBook({
//     id: this.state.book.id,
//     title: this.state.book.title,
//     author: this.state.book.authors,
//     link: this.state.books.infoLink,
//     thumbnail: this.state.books.imageLinks.thumbnail,
//     description: this.state.books.description,
//     publisheddate: this.state.books.publishedDate,
//   })
//     .catch(err => console.log(err));
// }

// addFavorite = event => {
//   event.preventDefault();
//   this.favById(this.props.match.params.id);         
// }



function SearchResults(props) {
  addFavorite = () => {

    myTitle= props.books.title
    myId = props.books.id
    myAuthor = props.books.authors
    document.getElementById("top-scores").textContent = myTitle;
    document.getElementById("top-scores").textContent = myId;
    document.getElementById("top-scores").textContent = myAuthor;
  }  
  return (

    <div>
      <ul className="list-group search-results">
        {props.books.map(result => (          
          <li key={result.id} className="list-group-item">
            <img alt={result.volumeInfo.title} width="200" height="220" className="img-fluid" src={result.volumeInfo.imageLinks == null ? 'https://lmtrain.github.io/lm-images/assets/images/books5.jpg' : result.volumeInfo.imageLinks.thumbnail} /><span>     </span>
            <Link key={result.id} to={"/index/" + result.id}>
            <span><button type="submit" onClick={props.handleDetailSubmit} className="btn btn-success">Detail</button></span><span>  </span>

            </Link>
            <ListItem key={result.id}>

                <span><button key={result.id} type="submit" onClick={() => props.addFavorite(result.id)} className="btn btn-success">Add to Favorite</button></span>

            </ListItem>           
            {/* <p>id : {result.id}</p> */}
            <p><b>Title             :</b> {result.volumeInfo.title}</p>
            <span><b>Author         :</b> {result.volumeInfo.authors} | |</span>
            <span><b>Published Date :</b> {result.volumeInfo.publishedDate}</span>        
          </li>          

        ))}

      </ul>
      <div id="top-sores"></div>

    </div>

  );

}



export default SearchResults;
