  // }

  loadUserPage = () => {
    API.getUsers()
      .then(res => {        
        this.setState({ user: res.data })
      }
      )
      .catch(err => console.log(err));
  };



  render() {

    return (
      <div>
        <h1 className="text-center">User Page</h1>        
      </div>
    );
  }
}

export default Signin;
