import React, { Component } from "react";
import { List, ListItem } from "../components/List";
import "./style.css";
import API from "../utils/API";



class Favorite extends Component {
  state = {
    books: {}   
  };

  // When the component mounts, get a list of all Favorite books in DB and update this.state.
  componentDidMount() {
    this.favById();
    this.loadBooks();    

  }

  loadBooks = () => {
    API.getBooks()
      .then(res => {
        console.log('LOADBOOKS', res)
        this.setState({ books: res.data, id: "", title: "", author: "", link: "", thumbnail: "", description: "", publisheddate: "", note: "",})
      }
      )
      .catch(err => console.log(err));
  };

  favById = () => {
    API.search(this.props.match.params.id)
      .then(res => this.setState({ books: res.data.volumeInfo }))
      this.saveFavBook()
      .catch(err => console.log(err));
  }

  // deleteBook = id => {
  //   API.deleteBook(id)
  //     .then(res => this.loadBooks())
  //     .catch(err => console.log(err));
  // };

  saveFavBook = () => {
    API.saveBook({
      id: this.state.books.id,
      title: this.state.books.title,
      authors: this.state.book.authors,
      link: this.state.books.infoLink,
      thumbnail: this.state.books.imageLinks.thumbnail,
      description: this.state.books.description,
      publisheddate: this.state.books.publishedDate,
    })
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div>
        <h1 className="text-center">Favorite Books</h1>

        <div>

          {this.state.books.length ? (
                <List>
                  {this.state.books.map(book => (
                    <ListItem>
                      <div className="card">
                        <div className="img-container">
                        <img
                          alt={book.title} width="150" height="280"
                          src={book.thumbnail}
                        />
                        </div>
                        <div className="content">
                          <ul>
                            <li>
                              <b>Title :</b> {book.title}
                            </li>
                            <li>
                              <b>Authors :</b>{book.author}
                            </li>
                            <li>
                              <b>Published Date :</b> {book.publishedDate}<span>||</span><span><b>Preview :</b> <a href={book.link} target="_blank" rel="noopener noreferrer">Link</a></span>
                            </li>
                          </ul>
                        </div>
                      </div>                     
                    </ListItem>
                  ))}
                </List>
              ) : (
                <h4>...Loading</h4>
              )}
        </div>

      </div>
    );
  }
}

export default Favorite;
