import React, { Component } from "react";
import API from "../utils/API";
import Container from "../components/Container";
import SearchForm from "../components/SearchForm";
import SearchResults from "../components/SearchResults";
// import { List } from "../components/List";
// import { Link } from "react-router-dom";

let myTitle = ""
let myId = ""
let myAuthor = ""
class Search extends Component {
  state = {
    search: "",
    id: "",      
    books: [],    
    title: "",
    author: "",
    link: "",
    thumbnail: "",
    description: "",
    publisheddate: "",           
    error: ""
  };

  searchForBooks = query => {
    API.search(query)
      .then(res => this.setState({ books: res.data.items }))
      .catch(err => console.log(err));
  }

  searchById = keyId => {
    API.search(keyId)
      .then(res => this.setState({ books: res.data.items }))
      .catch(err => console.log(err));
  }

  bookSaved = ()  => {
    console.log("Book SAVED");      

  }



  handleInputChange = event => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({
      [name]: value
    });
  };

  handleDetailSubmit = event => {
    event.preventDefault();
    this.searchById(this.state.id);         
  };

  handleFormSubmit = event => {
    event.preventDefault();
    this.searchForBooks(this.state.search);         
  };


  favoriteSubmit = (event) => {
    event.preventDefault();
    console.log(event.target);
    const id = event.target.id;
    console.log('id: ', id);
    // Find the id in the state
    const book = this.state.books.find((book) => book.id === id);
    console.log(book);
    console.log(book.volumeInfo.title);
    API.saveBook({
          bookid: book.id,
          title: book.volumeInfo.title,
          author: book.volumeInfo.authors,
          link: book.volumeInfo.infoLink,
          thumbnail: book.volumeInfo.imageLinks.thumbnail,
          description: book.volumeInfo.description,
          publisheddate: book.volumeInfo.publishedDate,
        })
          .then(res => this.bookSaved())
          .catch(err => console.log(err));
  }


  // favBooks = () => {
  //   API.getBooks()
  //     .then(res =>
  //       this.setState({ books: res.data, title: "", author: "", synopsis: "" })
  //     )
  //     .catch(err => console.log(err));
  // };

  render() {
    return (
      <div>
        <Container style={{ minHeight: "80%" }}>
          <h1 className="text-center">Search For Books</h1>

          <SearchForm
            search={this.state.search}
            handleFormSubmit={this.handleFormSubmit}
            handleInputChange={this.handleInputChange}            
          />
          <SearchResults books={this.state.books}
          favoriteSubmit={this.favoriteSubmit}
          handleFormSubmit={this.handleFormSubmit}
          />

        </Container>
      </div>

    );
  }
}

export default Search;
