import React, { Component } from "react";
// import { Link } from "react-router-dom";
// import Row from "../components/Row";
// import Col from "../components/Col";
// import Container from "../components/Container";
import "./style.css";
// import axios from "axios";
// import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";

let bookInfo =[]

class Detail extends Component {
  state = {
    book: {}
  }

  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc

  componentDidMount () {
    this.searchById();
    // console.log(this.state.book)
    // API.searchId(this.props.match.params.id)   
    //   .then(res => this.setState({ book: res.data }))
    //   .catch(err => console.log(err));
  }

  searchById = () =>{
    API.searchId(this.props.match.params.id)
    .then(res => this.setState({ book: res.data.volumeInfo }))      
    .catch(err => console.log(err));
  }

  render() {
    bookInfo.push(this.state.book)

    console.log(bookInfo)
    return (

      <div>
        <ul className="list-group search-results">

          <li key={this.state.book.id} className="list-group-item">
            <img alt={this.state.book.title} className="img-fluid" src={this.state.book.imageLinks == null ? 'https://lmtrain.github.io/lm-images/assets/images/books5.jpg' : this.state.book.imageLinks.thumbnail} /><span>     </span>
            <span><button type="submit" onClick={this.favoriteSubmit} className="btn btn-success">Add to Favorite</button></span>
            <p><b>id             :</b> {id}</p>
            <p><b>Title          :</b> {this.state.book.title}</p>
            <p><b>Authors        :</b>{this.state.book.authors}</p>
            <p><b>Description    :</b> {this.state.book.description}</p>
            <p><b>Published Date :</b> {this.state.book.publishedDate}</p>
            <p><b>Link           :</b> {this.state.book.infoLink}</p>

          </li>          
        </ul>
      </div>
    )
  }
}

export default Detail;
