import React, { Component } from "react";
// import Card from "../components/Card";
import API from "../utils/API";
import "./style.css";
import { List, ListItem } from "../components/List";
// import { Link } from "react-router-dom";
// import { Link } from "react-router-dom";
// import { Col, Row, Container } from "../components/Grid";
// import { List, ListItem } from "../components/List";
// import SearchResults from "../components/SearchResults";
// import Detail from "./Detail";

class Favorite extends Component {
  state = {
    books: [],

  };

  // When the component mounts, get a list of all Favorite books in DB and update this.state.
  componentDidMount() {
    this.loadBooks();    
    console.log(this.state);
  }

  loadBooks = () => {
    API.getBooks()
      .then(res => {
        console.log('LOADBOOKS', res)
        this.setState({ books: res.data, id: "", title: "", author: "", link: "", thumbnail: "", description: "", publisheddate: "", note: "",})
      }
      )
      .catch(err => console.log(err));
  };

  // deleteBook = id => {
  //   API.deleteBook(id)
  //     .then(res => this.loadBooks())
  //     .catch(err => console.log(err));
  // };

  // favoriteSubmit = event => {
  //   event.preventDefault();
  //   API.saveBook({
  //     title: this.state.result.volumeInfo.title,
  //     authors: this.state.result.volumeInfo.authors,
  //     link: this.state.result.volumeInfo.infoLink,
  //     thumbnail: this.state.result.volumeInfo.imageLinks.thumbnail,
  //     description: this.state.result.volumeInfo.description,
  //     publisheddate: this.state.result.volumeInfo.publishedDate,

  //   })
  //     .then(res => this.loadBooks())
  //     .catch(err => console.log(err));

  // };

  render() {
    return (
      <div>
        <h1 className="text-center">Favorite Books</h1>
        <h3 className="text-center">
          List of my favorite books!
        </h3>
        <div>

          {this.state.books.length ? (
                <List>
                  {this.state.books.map(book => (

                      <div className="card">
                        <div className="img-container">
                        <img
                          alt={book.title} width="150" height="280"
                          src={book.thumbnail}
                        />
                        </div>
                        <div className="content">
                          <ul>
                            <li>
                              <b>Title :</b> {book.title}
                            </li>
                            <li>
                              <b>Authors :</b>{book.author}
                            </li>
                            <li>
                              <b>Published Date :</b> {book.publishedDate}<span>||</span><span><b>Preview :</b> <a href={book.link} target="_blank" rel="noopener noreferrer">Link</a></span>
                            </li>
                          </ul>
                        </div>
                      </div>                     

                  ))}
                </List>
              ) : (
                <h3>No Results to Display</h3>
              )}
        </div>

      </div>
    );
  }
}

export default Favorite;
