            <article>
              <h1>Description</h1>
              <p>
                {this.state.book.description}
              </p>
            </article>
          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <Link to="/search">‚Üê Back to Authors</Link>
          </Col>
        </Row>
      </Container>
    );
  }
}

