import React from "react";
import "./style.css";



function Details(props) {
  
  return (
             
    <div>
      <ul className="list-group search-results">
        {props.books.map(detail => (          
          <li key={detail.id} className="list-group-item">
            <img alt={detail.volumeInfo.title} width="200" height="220" className="img-fluid" src={detail.volumeInfo.imageLinks == null ? 'https://lmtrain.github.io/lm-images/assets/images/books5.jpg' : detail.volumeInfo.imageLinks.thumbnail} /><span>     </span>
            
            
            <span><button key={detail.id} id={detail.id} type="submit" onClick={props.favoriteSubmit} className="btn btn-success">Add to Favorite</button></span>
                              
                   
           
            <p><b>Title             :</b> {detail.volumeInfo.title}</p>
            <span><b>Author         :</b> {detail.volumeInfo.authors} | |</span>
            <span><b>Published Date :</b> {detail.volumeInfo.publishedDate}</span>
            
          </li>          
          
        ))}
        
      </ul>
     
                  
    </div>
    
  );
  
}



export default Details;
