    //         <span><button id={detail.id} type="submit" onClick={props.favoriteSubmit} className="btn btn-success">Add to Favorite</button></span>
                              
                   
           
    //         <p><b>Title             :</b> {detail.volumeInfo.title}</p>
    //         <span><b>Author         :</b> {detail.volumeInfo.authors} | |</span>
    //         <span><b>Published Date :</b> {detail.volumeInfo.publishedDate}</span>
            
    //       </li>          
          
    //     ))}
        
    //   </ul>
     
                  
    // </div>
    
  );
  
}



export default Details;
